// watcher -> action -> workersaga
import { takeEvery, takeLatest, take, takeLeading, takeMaybe } from 'redux-saga/effects';



export default function* watcherSaga(){
    yield takeEvery('GET', workerSaga)
    yield takeEach
}


function* workerSaga(){
    alert("hi")
}
