import { IMAGES } from '../constants';

const errorReducer = (state = null ,action) => {
    switch(action.type){

        case IMAGES.FAIL:
            return action.error;       
        case IMAGES.LOAD:
        case IMAGES.SUCCESS:
            return null;        

        default:
            return state;
    }
}
export default errorReducer;
