import React from 'react';
import { connect } from 'react-redux';
import {loadDataByEmail} from '../actions'

class Profile extends React.Component{

    constructor(props){
        super(props);
        this.state={
            places:["mumbai","goa","pune","hyderabad"],
        }
        this.getData = this.getData.bind(this);
    }

    componentDidMount(){
        this.getData();
    }

    getData(){
        const data = {"value":"dsfdsfs@sdf.dsfdsjkf"}
        this.props.loadDataByEmail(data)
    }

    render(){
        const {response} = this.props;
        return(
            <div>
                {(response.status==="success")
                    ?<div>
                         <table class="table table-striped">
                    <thead class="thead-dark">
                        <tr>
                        <th scope="col">Fields</th>
                        <th scope="col">Data</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>Name</td>
                            <td>{response.data.name}</td>
                        </tr>
                        <tr>
                            <td>Email Id</td>
                            <td>{response.data.emailId}</td>
                        </tr>
                        <tr>
                            <td>Mobile</td>
                            <td>{response.data.mobile}</td>
                        </tr>
                        <tr>
                            <td>Address</td>
                            <td>{response.data.address}</td>
                        </tr>
                        <tr>
                            <td>City</td>
                            <td>{response.data.city}</td>
                        </tr>
                        <tr>
                            <td>State</td>
                            <td>{response.data.state}</td>
                        </tr>
                        <tr>
                            <td>Zip Code</td>
                            <td>{response.data.zipCode}</td>
                        </tr>
                    </tbody>
                    </table>   
                    </div>
                    :null
                }
            </div>
        )
    }
}


const mapStateToProps = ({response}) => ({
    response
})

const mapDispatchToProps = (dispatch)=>({
    loadDataByEmail : (email)=>dispatch(loadDataByEmail(email)),
})

export default connect(mapStateToProps, mapDispatchToProps)(Profile);
