import { USER } from '../constants';

let defaultState ={
}

const userDataReducer = (state = defaultState ,action) => {

    switch(action.type){

        case USER.postData:
            return{
                ...state, 
                ...action.response
            }    
        case USER.getData:
            return{
                ...state, 
                ...action.response
            }              
        default:
            return state

    }
}
export default userDataReducer;
