let defaultState={
}
const mainReducer=(state=defaultState,action)=>{

    switch(action.type){
        case "GET_USER_DATA":
            return{
                ...state,
                userData:action.userData
            }     
        case "SAVE_USER_DATA":
            return{
                ...state,
                userData:action.userData
            }                         
        default:
            return{
                ...state
            }
    }
}
export default mainReducer;
