import React from 'react';
import axios from 'axios';
import Cookies from 'universal-cookie';
import { Link } from 'react-router-dom';
import {connect} from "react-redux";
import * as actionCreators from "./actions/index.js"

class UserData extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            userData: []
        };
      }


     componentDidMount() {
        const cookies = new Cookies();
        if(cookies.get("emailKey") ===undefined  ){
            this.props.history.push("/") 
        } else{
             this.props.loadUserData(cookies.get("emailKey")).then((response)=>{

                console.log("**",response)

             })
        }
    }

    componentWillReceiveProps(props){
        const cookies = new Cookies();
        const response = props.userData;
        if(response.status==="success"){
            const userData = response.data;
            this.setState({ userData });
        } 
        else{
            this.props.history.push("/");
            cookies.remove("emailKey")
        }
    }

    render(){
        return(
            <div style={{textAlign:"center"}}>
                <h3>User Details</h3>


                <Link>Edit</Link>{' '}
                <Link to={'/'} onClick={()=>{
                    const cookies = new Cookies();
                    cookies.remove('emailKey');
                }}>New Profile
                </Link><br/><br/>


                <div>
                    <div>Name: {this.state.userData.name}</div>
                    <div>Email Id: {this.state.userData.emailId}</div>
                    <div>Mobile: {this.state.userData.mobile}</div>
                    <div>Address: {this.state.userData.address}</div>
                    <div>City: {this.state.userData.city}</div>
                    <div>State: {this.state.userData.state}</div>
                    <div>Zip Code: {this.state.userData.zipCode}</div>
                </div>

            </div>
        )
    }
}

// export default UserData;

const mapStateToProps=(state)=>{
    return state
};

export default connect (mapStateToProps, actionCreators)(UserData);
