import { RESPONSE } from '../constants';

const loadData = () => ({
    type : RESPONSE.LOAD
})

const setData = (response) => ({
    type : RESPONSE.SUCCESS,
    response,
})

const setError = (error) => ({
    type : RESPONSE.FAIL,
    error,
})

export { loadData, setData, setError};
