import React from 'react';

class Form extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            name: 'sarthak',
            emailId:'hello',
            mobile:'',
            address:'',
            city:'',
            state:'',
            zipCode:''
        };
        this.emailChange = this.emailChange.bind(this);

      }

    emailChange(event){
        this.setState({emailId : event.target.value});
    }
    render(){
        return(
            <div>
                <form>
                    Name:<br/>
                    <input type="text" name="name" value={this.state.name} onChange={this.nameChange}/>
                    <br/>
                    Email Id:<br/>
                    <input type="text" name="emailId" value={this.state.emailId} onChange={this.emailChange}/>
                    <br/>
                    Mobile:<br/>
                    <input type="text" name="mobile" value={this.state.mobile}/>
                    <br/>
                    Address:<br/>
                    <input type="text" name="address" value={this.state.address}/>
                    <br/>
                    City:<br/>
                    <input type="text" name="city" value={this.state.city}/>
                    <br/>
                    State:<br/>
                    <input type="text" name="state" value={this.state.state}/>
                    <br/>
                    Zip Code:<br/>
                    <input type="text" name="zipCode" value={this.state.zipCode}/>
                    <br/>
                    <input type="submit" value="Submit"/>
                </form>

            </div>
        )
    }
}

export default Form;
