import { RESPONSE } from '../constants';

const loadData = () => ({
    type : RESPONSE.LOAD
})

const loadDataByEmail = (email) => ({
    type : RESPONSE.LOAD,
    email
})

const sendUserData = (data) => ({
    type: RESPONSE.SEND,
    data
})

//TO SET RESPONSE FROM API
const setData = (response) => ({
    type : RESPONSE.SUCCESS,
    response,
})







const setError = (error) => ({
    type : RESPONSE.FAIL,
    error,
})

export { loadData, setData, setError, loadDataByEmail, sendUserData};
