import React from 'react';
import {connect} from 'react-redux';
import {loadData, loadEmail} from '../actions';

class Home extends React.Component{
    constructor(props){
        super(props);
        this.state={
            places:["mumbai","goa","pune","hyderabad"],
        }
    }

    render(){
        const {response} = this.props;
        return(
            <div>
                {/* {response.map((data)=> (
                    <div>{data}</div>
                ))} */}

                {response.length!==0
                    ?<div>
                        <p>Name:{response.data.name}</p>
                        <p>Email Id:{response.data.emailId}</p>
                        <p>Mobile:{response.data.mobile}</p>
                        <p>Address:{response.data.address}</p>
                        <p>City:{response.data.city}</p>
                        <p>State:{response.data.state}</p>
                        <p>Zip Code:{response.data.zipCode}</p>
                    </div>
                    :null
                }

                <button onClick={this.props.loadData}>Click</button>
            </div>
        )
    }
}

const mapStateToProps = ({response}) => ({
    response
})
const mapDispatchToProps = (dispatch)=>({
    loadData : ()=>dispatch(loadData()),
    loadEmail : ()=>dispatch(loadEmail("sa"))
})
export default connect(mapStateToProps, mapDispatchToProps)(Home);


