import React from 'react';
import Cookies from 'universal-cookie';
import { Link } from 'react-router-dom';
import {connect} from "react-redux";
import * as actionCreators from "./actions/index.js"

class UserData extends React.Component{
    constructor(props) {
        super(props);

        const cookies = new Cookies();
        const cookieValue = cookies.get("emailKey");

        this.state = {
            userData: [],
            cookieValue: cookieValue
        };
      }

    componentDidMount() {
        console.log(this.state.cookieValue)
        if(this.state.cookieValue === ''){
            this.props.history.push("/") 
        } else{
            this.props.loadUserData(this.state.cookieValue).then(()=>{
                const response = this.props.userData;
                if(response.status==="success"){
                    const userData = response.data;
                    this.setState({ userData });
                } 
                else{
                    const cookies = new Cookies();
                    this.props.history.push("/");
                    cookies.remove("emailKey")
                }
            })
        }
    }


    render(){
        return(
            <div style={{textAlign:"center"}}>
                <h3>User Details</h3>



                <Link 
                    to={{
                        pathname:'/',
                        dataToEdit: this.state.cookieValue
                    }} 
                    onClick={()=>{
                        // const cookies = new Cookies();
                        // cookies.remove('emailKey');
                    }}>Edit
                </Link>



                {' '}



                <Link 
                    to={{
                        pathname:'/'
                    }} 
                    onClick={()=>{
                        const cookies = new Cookies();
                        cookies.remove('emailKey');
                    }}>New Profile
                </Link>



                <br/><br/>


                <div>
                    <div>Name: {this.state.userData.name}</div>
                    <div>Email Id: {this.state.userData.emailId}</div>
                    <div>Mobile: {this.state.userData.mobile}</div>
                    <div>Address: {this.state.userData.address}</div>
                    <div>City: {this.state.userData.city}</div>
                    <div>State: {this.state.userData.state}</div>
                    <div>Zip Code: {this.state.userData.zipCode}</div>
                </div>

            </div>
        )
    }
}

const mapStateToProps=(state)=>{
    return state
};

export default connect (mapStateToProps, actionCreators)(UserData);
