// watcher -> action -> workersaga
import { takeEvery, takeLatest, take, takeLeading, takeMaybe ,put, call} from 'redux-saga/effects';
import {IMAGES} from '../constants';
import {saveUserData} from '../api'
import {setImages} from '../actions';

export default function* imagesSaga(){
    yield takeEvery(IMAGES.LOAD, loadImages)
}

function* loadImages(){
    console.log("Loading Images");
    const value = yield call(saveUserData)
    console.log(value.data.status)
    yield put (setImages(value.data))
}










