import { IMAGES } from '../constants';

const defaultState ={
    s:""
}

const imagesReducer = (state = defaultState ,action) => {

    switch(action.type){
        case IMAGES.SUCCESS:
            return[...state, ...action.images]                    
        default:
            return state

    }
}
export default imagesReducer;
