import React from 'react';
import {connect} from 'react-redux';
import {loadData, loadDataByEmail, sendUserData} from '../actions';

class Home extends React.Component{

    constructor(props){
        super(props);
        this.state={
            places:["mumbai","goa","pune","hyderabad"],
        }
        this.getData = this.getData.bind(this);
    }

    getData(){
        const data = {"value":"dsfdsfs@sdf.dsfdsjkf"}
        this.props.loadDataByEmail(data)
    }
    sendData(){
        const data = {"token":"","data":"null"}
        this.props.loadDataByEmail(data)
    }

    render(){
        const {response} = this.props;
        return(
            <div>

                {(response.status==="success")
                    ?<div>
                        <p>Name:{response.data.name}</p>
                        <p>Email Id:{response.data.emailId}</p>
                        <p>Mobile:{response.data.mobile}</p>
                        <p>Address:{response.data.address}</p>
                        <p>City:{response.data.city}</p>
                        <p>State:{response.data.state}</p>
                        <p>Zip Code:{response.data.zipCode}</p>
                    </div>
                    :null
                }
                <button onClick={this.getData}>Get Data</button>
                <button onClick={this.sendData}>Send Data</button>
            </div>
        )
    }
}

const mapStateToProps = ({response}) => ({
    response
})

const mapDispatchToProps = (dispatch)=>({
    loadDataByEmail : (email)=>dispatch(loadDataByEmail(email)),
    sendUserData : (dataToSend)=>dispatch(sendUserData(dataToSend)),
})
export default connect(mapStateToProps, mapDispatchToProps)(Home);


