import React from 'react';
import { connect } from 'react-redux';
import {loadDataByEmail} from '../actions'

class Profile extends React.Component{

    constructor(props){
        super(props);
        this.state={
            places:["mumbai","goa","pune","hyderabad"],
        }
        this.getData = this.getData.bind(this);
    }

    componentDidMount(){
        this.getData();
    }

    getData(){
        const data = {"value":"dsfdsfs@sdf.dsfdsjkf"}
        this.props.loadDataByEmail(data)
    }

    render(){
        const {response} = this.props;
        return(
            <div>
                {(response.status==="success")
                    ?<div>
                         <table class="table table-striped">
                    <thead class="thead-dark">
                        <tr>
                        <th scope="col">Fields</th>
                        <th scope="col">Data</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>Name</td>
                            <td>{this.state.userData.name}</td>
                        </tr>
                        <tr>
                            <td>Email Id</td>
                            <td>{this.state.userData.emailId}</td>
                        </tr>
                        <tr>
                            <td>Mobile</td>
                            <td>{this.state.userData.mobile}</td>
                        </tr>
                        <tr>
                            <td>Address</td>
                            <td>{this.state.userData.address}</td>
                        </tr>
                        <tr>
                            <td>City</td>
                            <td>{this.state.userData.city}</td>
                        </tr>
                        <tr>
                            <td>State</td>
                            <td>{this.state.userData.state}</td>
                        </tr>
                        <tr>
                            <td>Zip Code</td>
                            <td>{this.state.userData.zipCode}</td>
                        </tr>
                    </tbody>
                    </table>   
                        <p>Name:{response.data.name}</p>
                        <p>Email Id:{response.data.emailId}</p>
                        <p>Mobile:{response.data.mobile}</p>
                        <p>Address:{response.data.address}</p>
                        <p>City:{response.data.city}</p>
                        <p>State:{response.data.state}</p>
                        <p>Zip Code:{response.data.zipCode}</p>
                    </div>
                    :null
                }
            </div>
        )
    }
}


const mapStateToProps = ({response}) => ({
    response
})

const mapDispatchToProps = (dispatch)=>({
    loadDataByEmail : (email)=>dispatch(loadDataByEmail(email)),
})

export default connect(mapStateToProps, mapDispatchToProps)(Profile);
