// watcher -> action -> workersaga
import { takeEvery, takeLatest, take, takeLeading, takeMaybe ,put, call} from 'redux-saga/effects';



export default function* watcherSaga(){
    yield take('GET');
    yield call(workerSaga)
    yield take('GET');
    yield call(workerSaga)
}


function* workerSaga(){
    console.log("hi")
    yield put({type:"GET"});
}


/*
1)  takeEvery:
        yield takeEvery('GET', workerSaga);
2)  take:
        yield take('GET);
        yield call(workerSaga)
