import { IMAGES } from '../constants';

let defaultState={
    state : false
}

const loadingReducer = (state = defaultState ,action) => {
    switch(action.type){
        case IMAGES.LOAD:
            return true;     
        case IMAGES.LOAD_SUCCESS:
            return false;  
        case IMAGES.LOAD_FAIL:
            return false;             
        default:
            return state;
    }
}
export default loadingReducer;
