import rootReducer from "../reducers"
import {takeEvery, call} from 'redux-saga/effects';
import {RESPONSE} from '../constants';

function* workerSaga(){
    console.log("hi")
}

function* rootSaga(){
    console.log("hey")
    yield takeEvery(RESPONSE.LOAD, workerSaga)
}

export default rootSaga;
