// watcher -> action -> workersaga
import { takeEvery, takeLatest, take, takeLeading, takeMaybe ,put, call} from 'redux-saga/effects';



export default function* watcherSaga(){
    yield take('GET');
        yield call(loginSaga)
    yield take ("LOGOUT");
        yield call (logoutSaga)
}


async function* loginSaga(){
    await console.log("***********LOGIN");
    yield put ( { type:"LOGOUT" } )
}

function* logoutSaga(){
    console.log("LOGOUT**********");
}

