// watcher -> action -> workersaga
import { takeEvery, takeLatest, take, takeLeading, takeMaybe ,put, call} from 'redux-saga/effects';
import {USER} from '../constants';
import {saveUserData} from '../api'
import {postData} from '../actions';

export default function* imagesSaga(){
    yield takeEvery(USER.postData, postUserData)
}

function* postUserData(){
    const response = yield call(saveUserData)
    yield put(postData(response.data));
}





















