import rootReducer from "../reducers"
import {takeEvery} from 'redux-saga/effects';
import {RESPONSE} from '../constants';

function* workerSaga(){
    console.log("hi")
}

function* rootSaga(){
    takeEvery(RESPONSE.LOAD, workerSaga)
}

export default rootSaga;
