import { RESPONSE } from '../constants';

const loadData = () => ({
    type : RESPONSE.LOAD
})

const loadEmail = (email) => {
    alert(email);
}
//     {
//         type : RESPONSE.LOAD,
//         email
//     }
// )

const setData = (response) => ({
    type : RESPONSE.SUCCESS,
    response,
})

const setError = (error) => ({
    type : RESPONSE.FAIL,
    error,
})

export { loadData, setData, setError, loadEmail};
