// watcher -> action -> workersaga
import { takeEvery, takeLatest, take, takeLeading, takeMaybe ,put} from 'redux-saga/effects';



export default function* watcherSaga(){
    yield takeEvery('GET', workerSaga);
}


function* workerSaga(){
    alert("hi")
}
