import { RESPONSE } from '../constants';

const loadData = () => ({
    type : RESPONSE.LOAD
})



//GET USER DATA BY EMAIL
const loadDataByEmail = (email) => ({
    type : RESPONSE.LOAD,
    email
})

//POST USER DATA 
const sendUserData = (dataToSend) => ({
    type: RESPONSE.SEND,
    dataToSend
})

const updateUserData = (dataToUpdate) => ({
    type: RESPONSE.UPDATE,
    dataToUpdate
})

//TO SET RESPONSE FROM API
const setData = (response) => ({
    type : RESPONSE.SUCCESS,
    response,
})







const setError = (error) => ({
    type : RESPONSE.FAIL,
    error,
})

export { loadData, setData, setError, loadDataByEmail, sendUserData, updateUserData};
