import axios from "axios";

export function loadColor(){
    return(dispatch)=>{
        return axios.get("http://www.colr.org/json/color/random").then((response)=>{
            dispatch(changeColor("#"+response.data.new_color));
        })
    }
}
export function changeColor(color){
    return{
        type:"CHANGE_COLOR",
        color:color
    }
}



export function loadHistory(){
    return(dispatch)=>{
        return axios.get("http://localhost:8080/getReactAssigFormData/sm").then((response)=>{
            dispatch(changeHistory(response.data));
        })
    }
}
export function changeHistory(history){
    return{
        type:"CHANGE_HISTORY",
        history:history
    }
}
